import { motion } from "framer-motion";

function Home() {
  return (
    <div className="min-h-screen flex flex-col justify-center items-center text-white bg-gradient-to-br from-blue-900 via-black to-gray-900 px-4 text-center">
      {/* Profile Image */}
      <motion.img
        src="https://avatars.githubusercontent.com/u/9919?v=4" // ðŸ‘ˆ Replace this later with your image
        alt="Profile"
        className="w-40 h-40 rounded-full border-4 border-blue-500 shadow-lg mb-6 object-cover"
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1 }}
      />

      {/* Heading */}
      <motion.h1
        className="text-4xl md:text-6xl font-bold mb-3"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Hi, I'm <span className="text-yellow-400">Juttuka Prasad</span>
      </motion.h1>

      {/* Role Line */}
      <motion.p
        className="text-xl md:text-2xl mb-6 font-medium"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.6, duration: 1 }}
      >
        Frontend Developer | UI/UX Designer | Creative Artist
      </motion.p>

      {/* CTA Button */}
      <motion.a
        href="#about"
        className="bg-yellow-400 text-black font-semibold px-6 py-3 rounded-full hover:bg-yellow-300 transition"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1, duration: 1 }}
      >
        View My Work â†“
      </motion.a>

      {/* Optional Animation Background */}
      <motion.div
        className="absolute top-0 left-0 w-full h-full z-[-1] bg-gradient-to-br from-blue-900 via-black to-gray-900 opacity-60"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 2 }}
      />
    </div>
  );
}

export default Home;
