import { motion } from "framer-motion";

function Home() {
  return (
    <div className="h-screen flex flex-col items-center justify-center text-center bg-gradient-to-b from-blue-900 to-black text-white px-4">
      <motion.h1
        className="text-4xl md:text-6xl font-bold mb-4"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Hello, I'm <span className="text-yellow-400">Juttuka Prasad</span>
      </motion.h1>

      <motion.p
        className="text-lg md:text-2xl mb-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8, duration: 1 }}
      >
        Frontend Developer | UI/UX Designer | Creative Artist
      </motion.p>

      <motion.a
        href="#about"
        className="bg-yellow-400 text-black font-bold px-6 py-3 rounded-full hover:bg-yellow-300 transition"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.5, duration: 0.8 }}
      >
        View My Work â†“
      </motion.a>
    </div>
  );
}

export default Home;
