import { motion } from "framer-motion";
import { useEffect, useState } from "react";

const roles = [
  "Frontend Developer ðŸ’»",
  "UI/UX Designer ðŸŽ¨",
  "Creative Artist âœï¸",
];

function Home() {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % roles.length);
    }, 2500);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="h-screen bg-gradient-to-br from-blue-900 via-black to-gray-900 flex flex-col justify-center items-center text-white px-4 relative text-center">
      {/* Profile Image */}
      <motion.div
        className="w-40 h-40 mb-6 rounded-full overflow-hidden border-4 border-yellow-400 shadow-xl"
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1 }}
      >
        <img
          src="https://avatars.githubusercontent.com/u/9919?v=4"
          alt="Juttuka Prasad"
          className="w-full h-full object-cover"
        />
      </motion.div>

      {/* Name */}
      <motion.h1
        className="text-4xl md:text-6xl font-bold mb-2"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Hi, I'm <span className="text-yellow-400">Juttuka Prasad</span>
      </motion.h1>

      {/* Rotating Roles */}
      <motion.p
        className="text-xl md:text-2xl mb-4 text-gray-300 h-8"
        key={index}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        {roles[index]}
      </motion.p>

      {/* Buttons */}
      <motion.div
        className="flex gap-4 mt-4"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1 }}
      >
        <a
          href="#projects"
          className="bg-yellow-400 text-black font-semibold px-6 py-3 rounded-full hover:bg-yellow-300 transition"
        >
          View Projects
        </a>
        <a
          href="#contact"
          className="border border-yellow-400 px-6 py-3 rounded-full text-yellow-400 hover:bg-yellow-400 hover:text-black transition"
        >
          Hire Me
        </a>
      </motion.div>

      {/* Social Icons */}
      <motion.div
        className="flex gap-4 mt-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
      >
        <a href="https://github.com/" target="_blank" rel="noreferrer">
          <img src="https://cdn-icons-png.flaticon.com/512/25/25231.png" alt="GitHub" className="w-6 h-6" />
        </a>
        <a href="https://linkedin.com/" target="_blank" rel="noreferrer">
          <img src="https://cdn-icons-png.flaticon.com/512/174/174857.png" alt="LinkedIn" className="w-6 h-6" />
        </a>
      </motion.div>
    </div>
  );
}

export default Home;
