import { motion } from "framer-motion";
import { useEffect, useState } from "react";

const roles = [
  "Frontend Developer üíª",
  "UI/UX Enthusiast üé®",
  "Creative Designer ‚úçÔ∏è",
];

function Home() {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % roles.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative h-screen flex flex-col justify-center items-center text-white text-center overflow-hidden bg-black">
      {/* Animated Background Gradient */}
      <div className="absolute inset-0 animate-gradient bg-gradient-to-br from-blue-700 via-purple-800 to-black opacity-60 z-[-1]" />

      {/* Profile Image */}
      <motion.img
        src="https://avatars.githubusercontent.com/u/9919?v=4" // replace with your image later
        alt="Profile"
        className="w-40 h-40 rounded-full border-4 border-yellow-400 shadow-lg object-cover mb-4"
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1 }}
      />

      {/* Name & Fresher Badge */}
      <motion.h1
        className="text-4xl md:text-6xl font-bold"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Hi, I'm <span className="text-yellow-400">Juttuka Prasad</span>
      </motion.h1>
      <span className="mt-1 text-sm bg-yellow-500 text-black px-3 py-1 rounded-full font-semibold animate-pulse">
        Fresher üë®‚Äçüéì
      </span>

      {/* Rotating Role Animation */}
      <motion.p
        key={index}
        className="mt-4 text-lg md:text-2xl text-gray-200"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {roles[index]}
      </motion.p>

      {/* Action Buttons */}
      <motion.div
        className="flex gap-4 mt-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1 }}
      >
        <a
          href="#projects"
          className="bg-yellow-400 text-black px-6 py-3 rounded-full font-bold hover:bg-yellow-300 transition"
        >
          View Projects
        </a>
        <a
          href="#contact"
          className="border border-yellow-400 text-yellow-400 px-6 py-3 rounded-full hover:bg-yellow-400 hover:text-black transition"
        >
          Hire Me
        </a>
      </motion.div>

      {/* Social Icons */}
      <motion.div
        className="flex gap-5 mt-10"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
      >
        <a href="https://github.com/" target="_blank" rel="noreferrer">
          <img
            src="https://cdn-icons-png.flaticon.com/512/25/25231.png"
            alt="GitHub"
            className="w-6 h-6"
          />
        </a>
        <a href="https://linkedin.com/" target="_blank" rel="noreferrer">
          <img
            src="https://cdn-icons-png.flaticon.com/512/174/174857.png"
            alt="LinkedIn"
            className="w-6 h-6"
          />
        </a>
      </motion.div>
    </div>
  );
}

export default Home;
